package Programming;

public class EvenAndOddNoRange {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Even Number Range");
		for(int i=1;i<=10;i++) {
			if(i%2==0) {
				System.out.println(i);
			}
		}
		System.out.println("Odd Number Range");
		for(int i=1;i<=10;i++) {
			if(i%2!=0) {
				System.out.println(i);
			}
		}

	}

}
